<?php/** * @package Vpc * @subpackage Decorator */class Vpc_Decorator_Menu_BreadCrumbs_Component extends Vpc_Decorator_Menu_Abstract{    public function getTemplateVars()    {        $return = parent::getTemplateVars();        $pc = $this->_pageCollection;        $pages = array();        $page = $pc->getCurrentPage();        while ($page) {            $pages[] = $page;            $page = $pc->getParentPage($page);        }        $pages = array_reverse($pages);        $return['menu']['breadCrumbs'] = $this->_getMenuData($pages, array());        return $return;    }}