<?phpclass Vpc_Directories_Category_Directory_CategoriesController extends Vps_Controller_Action_Pool_PoolController{        private function _getCategoryComponent()    {    }    protected function _isAllowedComponent()    {        $class = substr($this->_getParam('class'), 0, strpos($this->_getParam('class'), '!'));        $c = Vps_Component_Data_Root::getInstance()            ->getComponentByDbId($this->_getParam('componentId'),                array('ignoreVisible'=>true, 'limit'=>1));        $c = $c->getChildComponent(array('componentClass'=>$class));        return $this->_isAllowedComponentById($c->componentId);    }    protected function _getPool()    {        $class = substr($this->_getParam('class'), 0, strpos($this->_getParam('class'), '!'));        return Vpc_Abstract::getSetting($class, 'pool');    }}