<?phpclass Vpc_Directories_Category_Directory_CategoriesController extends Vps_Controller_Action_Pool_PoolController{    protected function _isAllowedComponent()    {        return $this->_isAllowedComponentById($this->_getParam('componentId') . '_categories');    }    protected function _getPool()    {        $c = Vps_Component_Data_Root::getInstance()            ->getComponentByDbId($this->_getParam('componentId'),                array('ignoreVisible'=>true, 'limit'=>1));        $c = $c->getChildComponent('_categories');        return Vpc_Abstract::getSetting($c->componentClass, 'pool');    }}