<?phpclass Vpc_Directories_Category_Directory_Generator extends Vps_Component_Generator_Page_Table{    protected $_nameColumn = 'value';    public function select($parentData, array $constraints = array())    {        $select = parent::select($parentData, $constraints);        if (!$select) return null;        if (!($pool = Vpc_Abstract::getSetting($this->_class, 'pool'))) {            throw new Vps_Exception("No pool set for '{$this->_class}'");        }        $select->where('pool = ?', $pool);        return $select;    }}