<?phpclass Vpc_Directories_Category_Directory_ItemEditForm extends Vps_Form{    public function __construct($class, $id = null)    {        parent::__construct($class, $id);        $modelName = Vpc_Abstract::getSetting($class, 'categoryToItemModelName');        $model = Vps_Model_Abstract::getInstance($modelName);        $relToItemRef = $model->getReference('Item');        if (isset($relToItemRef['refModel'])) {            $itemModel = $relToItemRef['refModel'];        } else {            $itemModel = Vps_Model_Abstract::getInstance($relToItemRef['refModelClass']);        }        $this->setModel($itemModel);        $this->setIdTemplate('{0}');        $this->setCreateMissingRow(true);        $this->add(new Vps_Form_Field_MultiCheckbox($model, 'Category', trlVps('Categories')))            ->setPool(Vpc_Abstract::getSetting($class, 'pool'));    }}