<?phpclass Vpc_Directories_Category_Directory_ItemEditForm extends Vps_Form_NonTableForm{    public function __construct($class, $id = null)    {        parent::__construct($class, $id);        $tableName = Vpc_Abstract::getSetting($class, 'categoryToItemTableName');        $table = new $tableName();        $referenceMap = $table->info(Zend_Db_Table::REFERENCE_MAP);        $this->add(new Vps_Form_Field_PoolMulticheckbox($tableName, trlVps('Categories')))             ->setPool(Vpc_Abstract::getSetting($class, 'pool'))             ->setReferences($referenceMap['Item']);    }}