<?phpclass Vpc_Directories_Category_Directory_ItemEditForm extends Vps_Form{    public function __construct($class, $id = null)    {        parent::__construct($class, $id);        $this->setModel(new Vps_Model_FnF());        $this->setIdTemplate('{0}');        $this->setCreateMissingRow(true);        $modelName = Vpc_Abstract::getSetting($class, 'categoryToItemModelName');        $model = Vps_Model_Abstract::getInstance($modelName);//         d($model->getReference('Item'));//         $referenceMap = $table->info(Zend_Db_Table::REFERENCE_MAP);        $this->add(new Vps_Form_Field_PoolMulticheckbox($modelName, trlVps('Categories')))             ->setPool(Vpc_Abstract::getSetting($class, 'pool'));             ->setReferences('Item');    }}