<?phpabstract class Vpc_Directories_Category_Directory_ItemsToCategoriesModel    extends Vps_Db_Table_Abstract{    protected $_referenceMap = array(        'Category' => array(            'columns'           => array('category_id'),            'refTableClass'     => 'Vps_Dao_Pool',            'refColumns'        => array('id')        ),        'Item' => array()    );    protected function _setup()    {        parent::_setup();        if (!count($this->_referenceMap['Item'])) {            throw new Vps_Exception("Reference 'Item' must be set in Model '".get_class($this)."'");        }    }}