<?phpclass Vpc_Directories_Category_ShowCategories_Row extends Vps_Db_Table_Row_Abstract{    public function __get($columnName)    {        if ($columnName == 'category_id') {            $c = explode('#', $this->category);            return $c[1];        } else if ($columnName == 'directory_component_id') {            $c = explode('#', $this->category);            return $c[0];        } else {            return parent::__get($columnName);        }    }    public function __isset($columnName)    {        $allowed = array('category_id', 'directory_component_id');        if (in_array($columnName, $allowed)) {            return true;        } else {            return parent::__isset($columnName);        }    }}