<?phpclass Vpc_Directories_Item_Directory_Admin extends Vpc_Admin{    protected function _getContentClass()    {        throw new Vps_Exception_NotYetImplemented();    }    public function getExtConfig()    {        $contentClass = $this->_getContentClass();        $componentConfigs = array();        $contentEditComponents = array();        $cfg = Vpc_Admin::getInstance($contentClass)->getExtConfig();        foreach ($cfg as $k=>$c) {            $componentConfigs[$contentClass.'-'.$k] = $c;            $contentEditComponents[] = array(                'componentClass' => $contentClass,                'type' => $k            );        }        $cfgKeys = array_keys($cfg);        return array(            'items' => array(                'xtype'=>'vpc.directories.item.directory',                'controllerUrl' => $this->getControllerUrl(),                'title' => trlVps('Edit {0}', $this->_getSetting('componentName')),                'icon' => $this->_getSetting('componentIcon')->__toString(),                'contentClass' => $contentClass,                'contentType' => $cfgKeys[0],                'componentConfigs' => $componentConfigs,                'contentEditComponents' => $contentEditComponents,            )        );    }    public function delete($componentId)    {        $detail = Vpc_Abstract::getChildComponentClass($this->_class, 'detail');        Vpc_Admin::getInstance($detail)->delete($componentId);    }    /**     * Hilffunktion, kann in getExtConfig verwendet werden     */    protected function _getChildComponentPlugins($componentClasses)    {        $classes = array();        foreach ($componentClasses as $c) {            $classes = array_merge($classes, Vpc_Abstract::getChildComponentClasses($c));        }        $plugins = array();        foreach ($classes as $class) {            $plugin = Vpc_Admin::getComponentFile(                $class, 'Plugin', 'js', true            );            if ($plugin) {                $plugins[] = array(                    'pluginClass' => str_replace('_', '.', $plugin),                    'settings' => array(                        'controllerUrl' => Vpc_Admin::getInstance($class)->getControllerUrl()                    )                );            }        }        return $plugins;    }}