<?phpabstract class Vpc_Directories_Item_Directory_Component extends Vpc_Directories_List_Component{    public static function getSettings()    {        $ret = parent::getSettings();        $ret['generators']['detail'] = array(            'class' => 'Vps_Component_Generator_Table',            'component' => 'Vpc_Directories_Item_Detail_Component'        );        $ret['generators']['child']['component']['view'] = 'Vpc_Directories_List_View_Component';        $ret['useDirectorySelect'] = false;        return $ret;    }    protected function _getItemDirectory()    {        return $this->getData();    }    public final function callModifyItemData(Vps_Component_Data $item)    {        foreach (Vpc_Abstract::getChildComponentClasses(get_class($this)) as $c) {            if (Vpc_Abstract::hasSetting($c, 'hasModifyItemData')                && Vpc_Abstract::getSetting($c, 'hasModifyItemData')) {                call_user_func(array($c, 'modifyItemData'), $item, $c);            }        }    }}