<?phpabstract class Vpc_Directories_List_Component extends Vpc_Abstract_Composite_Component{    private $_itemDirectory = false;    public static function getSettings()    {        $ret = parent::getSettings();        $ret['generators']['child']['component']['view'] = 'Vpc_Directories_List_ViewPage_Component';        $ret['order'] = false;        return $ret;    }    final public function getItemDirectory()    {        if ($this->_itemDirectory === false) {            $this->_itemDirectory = $this->_getItemDirectory();            if (!$this->_itemDirectory) return $this->_itemDirectory;            if (is_string($this->_itemDirectory)) {                $c = $this->_itemDirectory;            } else {                $c = $this->_itemDirectory->componentClass;            }            if (!is_instance_of($c, 'Vpc_Directories_Item_Directory_Component')) {                throw new Vps_Exception("_getItemDirectory must return an Vpc_Directories_Item_Directory_Component data object or class-name, '{$c}' given");            }        }        return $this->_itemDirectory;    }    abstract protected function _getItemDirectory();    public function getSelect()    {        $itemDirectory = $this->getItemDirectory();        if (!$itemDirectory) return null;        if (is_string($itemDirectory)) {            $c = Vpc_Abstract::getComponentClassByParentClass($itemDirectory);            $ret = Vps_Component_Generator_Abstract::getInstance($c, 'detail')                ->select(null);        } else {            $ret = $itemDirectory->getGenerator('detail')                ->select($this->getItemDirectory());        }        if ($this->_getSetting('order') !== false) {            $ret->order($this->_getSetting('order'));        }        return $ret;    }}