<?phpabstract class Vpc_Directories_List_Component extends Vpc_Abstract_Composite_Component{    private $_itemDirectory = false;    public static function getSettings()    {        $ret = parent::getSettings();        $ret['generators']['child'] = array(            'component' => array(                'view' => 'Vpc_Directories_List_View_Component'            ),            'class' => 'Vps_Component_Generator_Static'        );        return $ret;    }    final public function getItemDirectory()    {        if ($this->_itemDirectory === false) {            $this->_itemDirectory = $this->_getItemDirectory();        }        return $this->_itemDirectory;    }    abstract protected function _getItemDirectory();    public function getSelect()    {        if (!$this->getItemDirectory()) return null;        return $this->getItemDirectory()->getComponent()->getSelect();    }}