<?phpclass Vpc_Directories_List_Feed_Component extends Vpc_Abstract_Feed_Component{    protected function _getSelect()    {        return $this->getData()->parent->getComponent()->getSelect();    }    protected function _getRssEntries()    {        $select = $this->_getSelect();        if (!$select) return array();        $select->group($select->getTableName().'.id');        $items = $this->getData()->parent->getComponent()->getItemDirectory()                    ->getChildComponents($select);        $ret = array();        foreach ($items as $item) {            $ret[] = $this->_getRssEntryByItem($item);        }        return $ret;    }    protected function _getRssEntryByItem(Vps_Component_Data $item)    {        return array(            'title' => $item->getTitle(),            'description' => '',            'link' => 'http://'.$_SERVER['HTTP_HOST'].$item->url        );    }    protected function _getRssTitle()    {        return parent::_getRssTitle().' - '.trlVps('Feed');    }}