<?phpclass Vpc_Directories_List_View_Admin extends Vpc_Admin{    protected function _onRowAction($row)    {        parent::_onRowAction($row);        if (Vpc_Abstract::getSetting($this->_class, 'viewCache')) {            $items = Vps_Component_Data_Root::getInstance()                ->getComponentsByClass($this->_class);            $tableNames = array();            foreach ($items as $item) {                $dir = $item->parent->getComponent()->getItemDirectory();                if (!$dir) continue;                if (is_object($dir)) $dir = $dir->componentClass;                $t = Vpc_Abstract::getSetting($dir, 'tablename');                if (!in_array($t, $tableNames)) {                    $tableNames[] = $t;                }            }            foreach ($tableNames as $t) {                if ($row->getTable() instanceof $t) {                    Vps_Component_Cache::getInstance()->cleanComponentClass($this->_class);                }            }        }    }}