<?phpclass Vpc_Directories_Top_Component extends Vpc_Directories_ListPage_Component{    public static function getSettings()    {        $ret = parent::getSettings();        $ret['showDirectoryClass'] = 'Vpc_Directories_Item_Directory_Component'; // nur fÃ¼r form        $ret['tablename'] = 'Vpc_Directories_Top_Model';        $ret['default'] = array();        $ret['limit'] = 5;        return $ret;    }    protected function _getItemDirectory()    {        $row = $this->_getRow();        if ($row && $row->directory_component_id) {            return Vps_Component_Data_Root::getInstance()->getComponentByDbId($row->directory_component_id);        }        return null;    }    public function getSelect()    {        $select = parent::getSelect();        if (!$select) return null;        $select->limit($this->_getSetting('limit'));        return $select;    }}