<?phpclass Vpc_Directories_Top_Form extends Vpc_Abstract_Form{    public function __construct($name, $class)    {        parent::__construct($name, $class);        $showDirectoryClass = Vpc_Abstract::getSetting($class, 'showDirectoryClass');        $directories = Vps_Component_Data_Root::getInstance()            ->getComponentsByClass($showDirectoryClass);        $values = array();        foreach ($directories as $directory) {            $values[$directory->dbId] = $directory->getTitle();        }        $this->fields->add(new Vps_Form_Field_Select('directory_component_id', trlVps('Show')))            ->setValues($values);    }}