<?php/** * Eventskomponente *  * @package Vpc * @subpackage Components */class Vpc_Events_Component extends Vpc_Abstract{    const NAME = 'Events';    protected function getChildPages($filename = '')    {        $pages = array();        for ($i = 2000; $i <= 2007; $i++) {            if ($filename != '' && $filename != $i) continue;            $pages[$i] = $this->createPage('Vpc_TextPic', 0, $i-1999);        }        return $pages;    }       public function getTemplateVars()    {        $ret = parent::getTemplateVars();        $ret['years']= array(2000, 2001, 2002, 2003, 2004, 2005, 2006, 2007);        $ret['template'] = 'Events.html';        return $ret;    }    } 