<?phpclass Vpc_News_Details extends Vpc_Abstract{    private $_id;    public $title;    private $_content;        public function setNewsId($id)    {        $this->_id = $id;    }        public function getTemplateVars()    {        $ret = parent::getTemplateVars();        $rows = $this->getDao()->getTable('Vps_Dao_News')->find($this->_newsId);        $row = $rows->current();                $this->_content = $this->createComponent('', $row->component_id);        $ret['content'] = $this->_content->getTemplateVars();        $ret['template'] = 'News/Details.html';        return $ret;    }        public function getComponentInfo()    {        return parent::getComponentInfo() + $this->_content->getComponentInfo();    }    }