<?phpclass Vpc_News_List_Abstract_Feed_Component extends Vpc_Directories_List_Feed_Component{    protected function _getRssEntries()    {        $ret = parent::_getRssEntries();        $ret = array();        foreach ($this->getNewsComponent()->getNews(15) as $n) {            $ret[] = array(                'title' => $n->name,                'link' => 'http://'.$_SERVER['HTTP_HOST'].$n->url,                'description' => $n->teaser,                'lastUpdate' => strtotime($n->publish_date)            );        }        return $ret;    }    protected function _getRssEntryByItem(Vps_Component_Data $item)    {        return array(            'title' => $item->getTitle(),            'link' => 'http://'.$_SERVER['HTTP_HOST'].$item->url,            'description' => $item->teaser,            'lastUpdate' => strtotime($item->publish_date)        );    }}