<?phpclass Vps_Auto_Data_Vpc_Table extends Vps_Auto_Data_Table_Parent{    public function load($row)    {        $table = new $this->_parentTable();        $key = array(            'page_id = ?' => $row->page_id,            'component_key = ?' => $row->component_key . '-' . $row->id        );        $row = $table->fetchAll($key)->current();        if ($row) {            $name = $this->_dataIndex;            return $row->$name;        } else {            return '';        }    }}