<?phpclass Vps_Auto_Field_NumberField extends Vps_Auto_Field_SimpleAbstract{    public function __construct($field_name = null, $field_label = null)    {        parent::__construct($field_name, $field_label);        $this->setXtype('numberfield');    }    protected function _addValidators()    {        parent::_addValidators();        if ($this->getMaxValue()) {            $this->addValidator(new Zend_Validate_LessThan($this->getMaxValue()));        }        if ($this->getMinValue()) {            $this->addValidator(new Zend_Validate_GreaterThan($this->getMinValue()));        }        if ($this->getAllowNegative() === false) {            $this->addValidator(new Zend_Validate_GreaterThan(-1));        }        if ($this->getAllowDecimals() === false) {            $this->addValidator(new Zend_Validate_Digits());        } else {            $this->addValidator(new Zend_Validate_Float());        }    }}