<?phpclass Vps_Controller_Action_Auto_Abstract extends Vps_Controller_Action{    protected $_buttons = array();    protected $_permissions;    public function init()    {        parent::init();        if (!isset($this->_permissions)) {            $this->_permissions = $this->_buttons;        }        $btns = array();        foreach ($this->_buttons as $k=>$i) {            if (is_int($k)) {                $btns[$i] = true;            } else {                $btns[$k] = $i;            }        }        $this->_buttons = $btns;        $perms = array();        foreach ($this->_permissions as $k=>$i) {            if (is_int($k)) {                $perms[$i] = true;            } else {                $perms[$k] = $i;            }        }        $this->_permissions = $perms;        //buttons/permissions abhÃ¤ngig von privileges in acl ausblenden/lÃ¶schen        $acl = $this->_getAcl();        $role = $this->_getUserRole();        $resource = $this->_getResourceName();        foreach ($this->_buttons as $k=>$i) {            if (!$acl->isAllowed($role, $resource, $k)) {                unset($this->_buttons[$k]);            }        }        foreach ($this->_permissions as $k=>$i) {            if (!$acl->isAllowed($role, $resource, $k)) {                unset($this->_permissions[$k]);            }        }    }}