<?phpclass Vps_User_Mirror extends Vps_Model_MirrorCache{    protected $_syncTimeField = 'last_modified';    protected $_rowClass = 'Vps_User_MirrorRow';    protected function _init()    {        $client = new Vps_Srpc_Client(array(            'serverUrl' => Vps_Registry::get('config')->service->users->url,            'extraParams' => array('applicationId' => Vps_Registry::get('config')->application->id)        ));        $this->_sourceModel = new Vps_Model_Service(array('client' => $client));        $this->_proxyModel = new Vps_Model_Db(array('table' => 'cache_users'));        parent::_init();    }}