<?phpif (file_exists('application/include_path')) {    define('VPS_PATH', file_get_contents('application/include_path'));} else  {    define('VPS_PATH', '/www/public/vps/library/');}set_include_path(get_include_path() . PATH_SEPARATOR . VPS_PATH);try {    require_once 'Vps/Assets/Loader.php';    Vps_Assets_Loader::load();    require_once VPS_PATH . '/Vps/Controller/Front.php';    Vps_Controller_Front::setUp();    Vps_Controller_Front::setUpDb();    $front = Vps_Controller_Front::getInstance();    $response = $front->dispatch();    $response->sendHeaders();    $response->outputBody();} catch (Exception $e){    echo '<pre>';    echo ($e->__toString());    echo '</pre>';}